import React, { Component } from 'react';


class Comments extends Component {

  constructor() {
    super();
    this.state = {
      title: '',
      description: '',
      _id: '',
      tasks: []
    };
    this.handleChange = this.handleChange.bind(this);
    this.addTask = this.addTask.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({
      [name]: value
    });
  }

  addTask(e) {
    e.preventDefault();
    const IDMOVIE = this.props.idmovie;
    console.log(this.props.idmovie);
    if(this.state._id) {
      fetch(`/api/tasks/${this.state._id}`, {
        method: 'PUT',
        body: JSON.stringify({
          title: this.state.title,
          description: this.state.description,
          idmovie: this.props.idmovie
        }),
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      })
        .then(res => res.json())
        .then(data => {
          this.setState({_id: '', title: '', description: ''});
          this.fetchTasks();
        });
    } else {
      fetch('/api/tasks', {
        method: 'POST',
        body: JSON.stringify(
            {
              id:this.state._id,
              title:this.state.title,
              description:this.state.description,
              idmovie: IDMOVIE
            }    
          ),
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      })
        .then(res => res.json())
        .then(data => {
          console.log(data);
          this.setState({title: '', description: ''});
          this.fetchTasks();
        })
        .catch(err => console.error(err));
    }

  }

  deleteTask(id) {
console.log('vas a borrar '+ id);
  }

  editTask(id) {
    fetch(`/api/tasks/${id}`)
      .then(res => res.json())
      .then(data => {
        console.log(data);
        this.setState({
          title: data.title,
          description: data.description,
          _id: data._id
        });
      });
  }

  componentDidMount() {
    this.fetchTasks();
    console.log(user.name);
  }

  fetchTasks() {
    fetch('/api/tasks')
      .then(res => res.json())
      .then(data => {
        this.setState({tasks: data});
        console.log(this.state.tasks);
      });
  }


    render(){ 

      return (
           <div className='col-md-6'>
              <div className='card p-4'>
                      <h3 className='mb-3'><b>Add Comments</b></h3>
                        <form onSubmit={this.addTask}>
                          <input className="form-control mb-2" name="title" onChange={this.handleChange} value={this.state.title} type="text" placeholder="Task Title" autoFocus/>
                          <textarea  className="form-control mb-2" name="description" onChange={this.handleChange} value={this.state.description} cols="30" rows="10" placeholder="Task Description"></textarea>
                          <button type="submit" className="btn btn-block btn-info">Send</button>
                        </form>
                        <div>
                        <h3 className='mt-4'><b>Others Comments</b></h3><hr/>
                        { 

                           this.state.tasks.map(task => {

                            return task.idmovie===this.props.idmovie ? (
                                <span key={task._id}>        
                                  <p>{task.description}</p><br/><hr/><br/>
                                  {/* <button onClick={() => this.deleteTask(task._id)} className="btn btn-danger">
                                  <i className="material-icons">delete</i> 
                                </button>
                                <button onClick={() => this.editTask(task._id)} className="btn btn-success" style={{margin: '4px'}}>
                                  <i className="material-icons">edit</i>
                                </button>     */}
                                </span>
                              ) : null;

                            })
                        }
                        </div>
                </div>  
            </div> 

      ) 

    }
  }


  export default Comments;
