import React from 'react';
import { useAuth0 } from '../contexts/auth0-context';
import {Link} from 'react-router-dom';

export default function Header() {
  const { isLoading, user, loginWithRedirect, logout } = useAuth0();

  return (
    <header>
      <nav className="bg-dark">
        <div className="container">
              <Link to='/' className='text-white m-3' ><i className='fa fa-home' ></i> MovieApp Test</Link>

              {!isLoading && !user && (
                <button onClick={loginWithRedirect} className="navbar-item">
                  Login
                </button>
              )}

              {!isLoading && user && (
                <>
                  <button className="btn btn-outline-light m-3 text-white">{user.name}</button>
                  <button
                    onClick={() => logout({ returnTo: window.location.origin })}
                    className="btn btn-outline-light m-3 text-white"
                  >
                    Logout
                  </button>
                </>
              )}
        </div>
      </nav>
    </header>
  );
}
